  }
  .site-footer .-link:hover, .site-footer--copyright p a,
  .site-footer--copyright p a:visited {
    color: #ddd !important;
  }
  .site-footer .-title, .site-footer .-title a,
  .site-footer--copyright p a:hover {
    color: #eee !important;
  }
}
@-moz-document regexp("https?://stackoverflow\.com/(enterprise|teams|talent|advertising).*") {
  img.wmx100 {
    filter: invert(1) hue-rotate(180deg) contrast(100%) brightness(400%);
    mix-blend-mode: exclusion;
  }
  .p-bg-white-tint, .p-bg-blue-tint {
    background: #222;
  }
  .bg-black-300, .h\:bg-black-300:hover, .f\:bg-black-300:focus {
    background-color: #444 !important;
  }
  .p-bg-blue, .s-navigation--item.is-selected {
    background-color: rgba(79, 140, 201, .6) !important;
    background-color: rgba(/*[[base-color-rgb]]*/, .6) !important;
  }
  .p-fc-blue, h2.p-fc-blue {
    color: /*[[base-color]]*/ !important;
  }
  .fc-powder-900 {
    color: #999 !important;
  }
  .p-bg-beige-pastel, .p-bg-blue-pastel {
    background: #282828;
  }
  .p-bg-yellow {
    background: #ffb500;
    color: #111 !important;
  }
  .h\:p-bg-yellow-tint:hover {
    background: #f1ab00 !important;
  }
}
@-moz-document regexp("https?://stackexchange.com/(about|performance|leagues|sites\#)\.*"), regexp("https?://stackexchange.com/about/(moderators|security)\.*") {
  .gv-size-huge .btn {
    background: #555 !important;
    color: #ddd !important;
    text-shadow: none;
  }
  .gv-size-huge .btn:hover {
    background: /*[[base-color]]*/ !important;
    color: #eee !important;
    text-shadow: none;
  }
  .section-2 .data .state {
    color: #888;
  }
  #mainContainer, .mainTitle span, .title span, .legend, .stat.percentage .peak,
  .section-8 .mainTitle h2, .section-8 .mainTitle span {
    color: #aaa;
  }
  .stat.throughput .number {
    color: #146dc7;
  }
  .stat.percentage {
    border-color: #555;
  }
  .section-8 .tag-link {
    background: #111;
    border-color: #444;
    color: #ccc !important;
  }
  .section-8 .tag-link:hover {
    background: #222;
    color: #ddd !important;
  }
  .section-3 .illustration {
    border-top-color: #666;
  }
  .mods-item, .league-container {
    border-color: #444;
  }
  .bc-black-3 {
    border-color: #444 !important;
  }
  .fc-blue-600 {
    color: /*[[base-color]]*/ !important;
  }
  .introBlock .illustration .img1, img[src*="db.svg"] {
    filter: invert(1) hue-rotate(180deg) contrast(90%) brightness(300%);
    mix-blend-mode: exclusion;
  }
  img[src="https://cdn.sstatic.net/stackexchange/Img/performance/svg/server.svg?v=8469d56f8b0e"] {
    filter: invert(1) hue-rotate(180deg) contrast(350%) brightness(60%);
    mix-blend-mode: exclusion;
  }
  a[href*="stackoverflow"] img[src*="Img/about/logo-sotalent.svg"],
  a[href*="stackoverflow"] img[src*="Img/about/logo-so.svg"] {
    filter: invert(100%) hue-rotate(180deg) !important;
  }
}

